'use strict';
const execSync = require('child_process').execSync;

module.exports = function(stringToProcess,portNumber,annotatorsString,outputFormatString) {
  let termOutput = execSync('output=$(wget --post-data '+"'"+stringToProcess+"' "+"'192.168.100.105:"+portNumber+'/?properties={"annotators": '+'"'+annotatorsString+'", "outputFormat": "'+outputFormatString+'"'+"}' -qO -) && echo $output",{ encoding: 'utf8' });
  return termOutput;
};

